<?phpinclude \view('adm_inc__header');?><template id="v_add_establish">  <div class="example ivu-row">    <div class="example-demo ivu-col ivu-col-span-10">      <div class="example-case">        <h1>注册公司</h1>      <i-form v-ref:form-validate :model="formValidate" :rules="ruleValidate" :label-width="80">        <Form-item label="公司名称" prop="name">            <i-input :value.sync="formValidate.name" placeholder="请输入公司名称"></i-input>        </Form-item>        <Form-item label="注册账号(手机号)" prop="phone">            <i-input :value.sync="formValidate.phone" placeholder="请输入注册账号"></i-input>        </Form-item>        <Form-item label="设置登录密码" prop="passwd">            <i-input :value.sync="formValidate.passwd" placeholder="请输入登录密码"></i-input>        </Form-item>        <Form-item label="设置管理员昵称" prop="manager_name">            <i-input :value.sync="formValidate.manager_name" placeholder="请输入管理员昵称"></i-input>        </Form-item>        <Form-item label="设置管理员真实姓名" prop="manager_real_name">            <i-input :value.sync="formValidate.manager_real_name" placeholder="请输入管理员真实姓名"></i-input>        </Form-item>        <Form-item>            <i-button type="primary" @click="handleSubmit('formValidate')" icon="share">提交</i-button>            <i-button type="ghost" @click="handleReset('formValidate')" style="margin-left: 8px">重置</i-button>        </Form-item>      </i-form>    </div>    </div>       <div class="example-demo ivu-col ivu-col-span-10 ivu-col-split-right">      <div class="example-case" style="margin-top:47px;">        <i-col offset="2">            <Card shadow>                <p slot="title">小提示</p>                <p>1.所有内容都必须填写完整哦</p>                <p>2.账号建议是管理员的手机号</p>                <p>3.公司账号密码就是管理员登陆凭证</p>                <p>4.公司重名或者账号重复会导致注册失败的</p>            </Card>        </i-col>      </div>     </div>  </div></template><script>$$.vue({    el:'#v_add_establish',    data: function(){      return {        formValidate: {          name:'',          phone:'',          passwd:'',          manager_name:'',        },        ruleValidate:{          name: [              { required: true, message: '姓名不能为空', trigger: 'blur' },          ],          phone: [              { required: true, message: '账号不能为空', trigger: 'blur' },          ],          passwd: [              { required: true, message: '密码不能为空', trigger: 'blur' },          ],          manager_name: [              { required: true, message: '管理员昵称不能为空', trigger: 'blur' },          ],          manager_real_name:[              { required: true, message: '管理员真实姓名不能为空', trigger: 'blur' },          ],      },      }    },    methods:{      handleSubmit:function (name) {        var self = this          this.$refs[name].validate((valid) => {              if (valid) {                // alert(self.formValidate['name'])                  $$.ajax({                      methods:'get',                      url:'/adm_company__establish/aj_add_establish',                      data:{                        name: self.formValidate['name'],                        phone: self.formValidate['phone'],                        passwd: self.formValidate['passwd'],                        manager_name: self.formValidate['manager_name'],                        manager_real_name: self.formValidate['manager_real_name'],                      },                      succ: function (data){                        self.$Message.success('提交成功!')                        // window.location.reload()                      },                      fail: function (msg){                        self.$Message.error('公司或账号已存在!')                      },                  })              } else {                  self.$Message.error('提交失败!')              }          })      },      handleReset:function (name) {          this.$refs[name].resetFields()      }    }})</script><?phpinclude \view('adm_inc__footer');?>